mapper.select:
SELECT * FROM ( SELECT listing_info.listingGuid, listing_info.listingCode, listing_info.ListingFullName, listing_info.Issuer, listing_info.ListingMoney, listing_info.ExchangeId, listing_info.listingStatus,
IFNULL(bizimport_summary.applyMoney,0) applyMoney, IFNULL(bizimport_summary.applyNum,0) applyNum FROM listing_info LEFT JOIN (SELECT bizimport_apply.ListingInfoId, SUM(bizimport_summary.TotalMoney) applyMoney, COUNT(bizimport_apply.Id) applyNum FROM bizimport_apply LEFT JOIN bizimport_summary ON (bizimport_apply.Id = bizimport_summary.BizImportApplyId) GROUP BY bizimport_apply.ListingInfoId ) bizimport_summary ON (bizimport_summary.ListingInfoId = listing_info.Id) ) t WHERE t.listingStatus = 1
AND t.ListingCode = #{listingCode}
AND t.ExchangeId = #{exchangeId}
AND t.listingFullName like "%"#{listingFullName}"%"
